// SPDX-License-Identifier: MIT
pragma solidity ^0.5.0;

contract Funds {
    struct Fund {
        uint money;
        string numberOfBeneficiaries;
        string tag;
        address creator;
    }

    Fund[] public funds;
    mapping(address => uint[]) public creatorToFunds;

    event FundCreated(uint indexed fundIndex, uint money, string numberOfBeneficiaries, string tag, address creator);

    function createFund(uint _money, string memory _numberOfBeneficiaries, string memory _tag) public {
        Fund memory newFund = Fund(_money, _numberOfBeneficiaries, _tag, msg.sender);
        funds.push(newFund);
        uint fundIndex = funds.length - 1;
        creatorToFunds[msg.sender].push(fundIndex);
        emit FundCreated(fundIndex, _money, _numberOfBeneficiaries, _tag, msg.sender);
    }

    function getFundsByCreator(address _creator) public view returns (uint[] memory) {
        return creatorToFunds[_creator];
    }
}
