pragma solidity ^0.5.0;

contract Government {
    uint public fundCount = 0;

    struct Fund {
        uint id;
        string content;
        uint tag;
        uint money;
    }

    mapping(uint => Fund) public funds;

    constructor() public {
        createFund("Government Studies Fund - 2024", 1, 30);
    }

    function createFund(string memory _content, uint _tag, uint _money) public {
        fundCount++;
        funds[fundCount] = Fund(fundCount, _content, _tag, _money);
    }
}
